import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { StatusBar } from 'expo-status-bar';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { Provider } from 'react-redux';
import { store } from './src/store/store';
import { ThemeProvider } from './src/context/ThemeContext';
import { Button, View, Text, StyleSheet, Alert } from 'react-native'; // Import necessary components

// Import screens
import HomeScreen from './src/screens/HomeScreen';
import StockDetailScreen from './src/screens/StockDetailScreen';
import ExploreScreen from './src/screens/ExploreScreen';
import WatchlistScreen from './src/screens/WatchlistScreen';
import ChatScreen from './src/screens/ChatScreen';

// Import MongoDB service functions
import { insertDocument, connectToMongo, getAllStocks } from './src/services/mongoService';

export type RootStackParamList = {
  MainTabs: undefined;
  StockDetail: { stockId: string };
};

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator<RootStackParamList>();

const MainTabs = () => (
  <Tab.Navigator
    screenOptions={{
      headerShown: false,
      tabBarActiveTintColor: '#2563eb',
      tabBarInactiveTintColor: '#6b7280',
      tabBarStyle: {
        backgroundColor: '#ffffff',
        borderTopColor: '#e5e7eb',
      },
    }}
  >
    <Tab.Screen name="Home" component={HomeScreen} />
    <Tab.Screen name="Explore" component={ExploreScreen} />
    <Tab.Screen name="Watchlist" component={WatchlistScreen} />
    <Tab.Screen name="Chat" component={ChatScreen} />
  </Tab.Navigator>
);

const App = () => {
  // Fetch stocks when the app loads
  React.useEffect(() => {
    const fetchStocks = async () => {
      try {
        const stocks = await getAllStocks();
        console.log('Fetched stocks:', stocks);
      } catch (error) {
        console.error('Error fetching stocks in App.tsx:', error);
      }
    };

    fetchStocks();
  }, []); // Empty dependency array ensures this runs once on mount

  return (
    <SafeAreaProvider>
      <Provider store={store}>
        <ThemeProvider>
          <NavigationContainer>
            <StatusBar style="dark" backgroundColor="#ffffff" />
            <Stack.Navigator screenOptions={{ headerShown: false }}>
              <Stack.Screen name="MainTabs" component={MainTabs} />
              <Stack.Screen name="StockDetail" component={StockDetailScreen} />
            </Stack.Navigator>
          </NavigationContainer>
        </ThemeProvider>
      </Provider>
    </SafeAreaProvider>
  );
};

export default App;
